import React, { useState } from 'react';
import { Typography, Divider, TextField, Grid, Button } from '@material-ui/core/';
import Box from '../components/Box';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  formDisplay: {
    '& > *': {
      margin: theme.spacing(1)
    }
  },
  formDisplaySingleLine: {
    '& > *': {
      margin: theme.spacing(1)
    }
  },
  profileImage: {
    height: 150,
    width: 150,
    borderRadius: '50%',
    margin: '20px 0'
  },
  flexBox: {
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'column'
  }
}));

export default function Account() {
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phone, setPhone] = useState('');
  const [bio, setBio] = useState('');
  const classes = useStyles();

  return (
    <div>
      <Typography variant="h4">Account</Typography>
      <Divider style={{ marginBottom: 20 }} />

      <Grid container spacing={2}>
        <Grid item xs={12} sm={12} md={6} lg={12}>
          <Box>
            <Typography variant="h5">Public Information</Typography>
            <TextField
              required
              variant="outlined"
              margin="normal"
              id="username"
              label="Username"
              name="username"
              type="text"
              autoComplete="username"
              value={username}
              onChange={e => setUsername(e.target.value)}
            />
            <TextField
              required
              multiline
              rows="4"
              variant="outlined"
              margin="normal"
              id="bio"
              label="Bio"
              name="bio"
              type="text"
              autoComplete="bio"
              value={bio}
              onChange={e => setBio(e.target.value)}
            />
          </Box>
        </Grid>

        <Grid zeroMinWidth item xs={12} sm={12} md={6} lg={6}>
          <Box>
            <Typography variant="h5">Private Information</Typography>
            <div className={classes.formDisplay}>
              <TextField
                required
                style={{ width: 234 }}
                variant="outlined"
                margin="normal"
                id="firstName"
                label="First name"
                name="firstName"
                type="text"
                autoComplete="firstName"
                value={firstName}
                onChange={e => setFirstName(e.target.value)}
              />
              <TextField
                required
                style={{ width: 234 }}
                variant="outlined"
                margin="normal"
                id="lastName"
                label="Last name"
                name="lastName"
                type="text"
                autoComplete="lastName"
                value={lastName}
                onChange={e => setLastName(e.target.value)}
              />
            </div>
            <div className={classes.formDisplaySingleLine}>
              <TextField
                required
                fullWidth
                variant="outlined"
                margin="normal"
                id="email"
                label="Email"
                name="email"
                type="email"
                autoComplete="email"
                value={email}
                onChange={e => setEmail(e.target.value)}
              />
            </div>
            <div className={classes.formDisplaySingleLine}>
              <TextField
                fullWidth
                variant="outlined"
                margin="normal"
                id="phone"
                label="Phone"
                name="phone"
                type="text"
                autoComplete="phone"
                value={phone}
                onChange={e => setPhone(e.target.value)}
              />
            </div>
          </Box>
        </Grid>

        <Grid item xs={false} sm={12} md={4} lg={3}>
          <Box>
            <Typography variant="h5">Profile Image</Typography>
            <div className={classes.flexBox}>
              <img
                src={require('../assets/images/alec.jpg')}
                alt="profile"
                className={classes.profileImage}
              />
              <Button fullWidth>Upload</Button>
            </div>
          </Box>
        </Grid>

        <Grid item xs={12} sm={12} md={6} lg={6}>
          <Box>
            <Typography variant="h5">Reset Password</Typography>
            <Typography variant="p">Please enter your current password to get started.</Typography>
            <TextField
              variant="outlined"
              margin="normal"
              id="password"
              label="Password"
              name="password"
              type="password"
              autoComplete="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </Box>
        </Grid>
      </Grid>
    </div>
  );
}
