import axios from 'axios';
import { GET_ERRORS, SET_USER_STREAKS } from './types';

export const getUserStreaks = () => dispatch => {
  axios
    .get('/api/streak')
    .then(res => {
      dispatch({
        type: SET_USER_STREAKS,
        payload: res.data
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      });
    });
};

export const createStreak = streakData => {
  if (streakData.streakType === 'individual') {
    axios
      .post('/api/streak/individual', streakData)
      .then(response => {
        if (response.data.streakCreated) {
          dispatch(getUserStreaks());
        }
      })
      .catch(err => {
        console.log(err);
      });
  } else if (streakData.streakType === 'squad') {
    axios.post('/api/streak/squad', streakData);
  }
};
