import React, { useState } from 'react';
import { Typography, Divider, Button, Grid } from '@material-ui/core/';
import AddIcon from '@material-ui/icons/Add';
import Box from '../components/Box';

export default function Squad() {
  const [squad, setSquad] = useState([
    {
      id: 0,
      firstName: 'Kelsey',
      lastName: 'Down',
      avatar: 'https://randomuser.me/api/portraits/women/41.jpg',
      bio: "This is a sample bio line. I'm an editor!"
    }
  ]);

  return (
    <div>
      <Typography variant="h4">Squad</Typography>
      <Divider style={{ marginBottom: 20 }} />

      <Button startIcon={<AddIcon />}>Add User</Button>

      <Grid container spacing={2}>
        {squad.map((item, idx) => (
          <Grid item xs={12} key={idx}>
            <Box>
              <ListItem alignItems="flex-start">
                <ListItemAvatar>
                  <Avatar alt="Remy Sharp" src={item.avatar} />
                </ListItemAvatar>
                <ListItemText
                  primary={item.user}
                  secondary={
                    <React.Fragment>
                      <Typography component="span" variant="body2" color="textPrimary"></Typography>
                      {item.comment}
                    </React.Fragment>
                  }
                />
              </ListItem>
            </Box>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}
