import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Typography, Divider, TextField, Grid, Button, Select } from '@material-ui/core/';
import Box from '../components/Box';
import { makeStyles } from '@material-ui/core/styles';
import CloudUploadOutlinedIcon from '@material-ui/icons/CloudUploadOutlined';
import { updateUserProfile, updatePassword } from '../actions/profileActions';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  formDisplay: {
    '& > *': {
      margin: theme.spacing(1)
    }
  },
  formDisplaySingleLine: {
    '& > *': {
      margin: theme.spacing(1)
    }
  },
  profileImage: {
    height: 150,
    width: 150,
    borderRadius: '50%',
    margin: '20px 0'
  },
  flexBox: {
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'column'
  },
  subText: {
    color: 'rgba(0,0,0,0.7)',
    fontSize: '0.7rem',
    textAlign: 'center'
  }
}));

export default function NewProfile() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phone, setPhone] = useState('');
  const [bio, setBio] = useState('');
  const [company, setCompany] = useState('');
  const [site, setSite] = useState('');
  const [state, setState] = useState('');
  const [city, setCity] = useState('');
  const [handle, setHandle] = useState('');

  const errors = useSelector(state => state.errors);

  useEffect(() => {
    setHandle(profile.handle);
  }, [profile.handle]);

  useEffect(() => {
    setBio(profile.bio);
  }, [profile.bio]);

  useEffect(() => {
    setCity(profile.city);
  }, [profile.city]);

  useEffect(() => {
    setState(profile.state);
  }, [profile.state]);

  useEffect(() => {
    setCompany(profile.company);
  }, [profile.company]);

  useEffect(() => {
    setSite(profile.site);
  }, [profile.site]);

  useEffect(() => {
    setFirstName(profile.firstName);
  }, [profile.firstName]);

  useEffect(() => {
    setLastName(profile.lastName);
  }, [profile.lastName]);

  useEffect(() => {
    setPhone(profile.phone);
  }, [profile.phone]);

  const classes = useStyles();

  const dispatch = useDispatch();

  const handleSavePublicInformation = () => {
    const publicInformation = { handle, bio, city, state, company, site };
    dispatch(updateUserProfile(publicInformation));
  };

  const handleSavePrivateInformation = () => {
    const privateInformation = { firstName, lastName, phone };
    dispatch(updateUserProfile(privateInformation));
  };

  return (
    <div>
      <Typography variant="h4">New Profile</Typography>
      <Divider style={{ marginBottom: 20 }} />

      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Box>
            <Typography variant="h5">Public Information</Typography>
            <div className={classes.formDisplaySingleLine}>
              <TextField
                fullWidth
                required
                variant="outlined"
                margin="normal"
                id="handle"
                label="Handle"
                name="handle"
                type="text"
                autoComplete="handle"
                value={handle}
                onChange={e => setHandle(e.target.value)}
              />
            </div>

            <div className={classes.formDisplaySingleLine}>
              <TextField
                fullWidth
                multiline
                rows="4"
                variant="outlined"
                margin="normal"
                id="bio"
                label="Bio"
                name="bio"
                type="text"
                autoComplete="bio"
                value={bio}
                onChange={e => setBio(e.target.value)}
              />
            </div>
            <div className={classes.formDisplay}>
              <TextField
                variant="outlined"
                margin="normal"
                id="city"
                label="City"
                name="city"
                type="text"
                autoComplete="city"
                value={city}
                onChange={e => setCity(e.target.value)}
              />
              <Select
                native
                style={{ width: 100 }}
                variant="outlined"
                margin="normal"
                id="state"
                label="State"
                name="state"
                type="text"
                autoComplete="state"
                value={state}
                onChange={e => setState(e.target.value)}
