import React from 'react';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import { makeStyles } from '@material-ui/core/styles';
function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    '& > * + *': {
      marginTop: theme.spacing(2)
    }
  }
}));

const styles = {
  svg: {
    fill: '#fff',
    fontSize: 14,
    marginRight: 12,
    opacity: 0.9,
    width: 20,
    height: 20
  },
  success: {
    backgroundColor: '#43a047',
    borderRadius: 4,
    boxShadow:
      '0px 3px 5px -1px rgba(0,0,0,0.2), 0px 6px 10px 0px rgba(0,0,0,0.14), 0px 1px 18px 0px rgba(0,0,0,0.12)',
    color: '#fff',
    fontSize: 14,
    letterSpacing: 0.14994,
    lineHeight: '20.02px',
    padding: '14px 16px',
    margin: 24,
    width: 300,
    maxWidth: '90%'
  },
  info: {
    backgroundColor: '#1976d2',
    borderRadius: 4,
    boxShadow:
      '0px 3px 5px -1px rgba(0,0,0,0.2), 0px 6px 10px 0px rgba(0,0,0,0.14), 0px 1px 18px 0px rgba(0,0,0,0.12)',
    color: '#fff',
    fontSize: 14,
    letterSpacing: 0.14994,
    lineHeight: '20.02px',
    padding: '14px 16px',
    margin: 24,
    width: 300,
    maxWidth: '90%'
  },

  error: {
    backgroundColor: '#d32f2f',
    borderRadius: 4,
    boxShadow:
      '0px 3px 5px -1px rgba(0,0,0,0.2), 0px 6px 10px 0px rgba(0,0,0,0.14), 0px 1px 18px 0px rgba(0,0,0,0.12)',
    color: '#fff',
    fontSize: 14,
    letterSpacing: 0.14994,
    lineHeight: '20.02px',
    padding: '14px 16px',
    margin: 24,
    width: 300,
    maxWidth: '90%'
  }
};

const AlertTemplate = ({ options, message }) => (
  <div>{options.type === 'success' && <Alert severity="success">{message}</Alert>}</div>
);

export default AlertTemplate;
