import React, { useState } from 'react';
import { Typography, Divider, TextField, Grid } from '@material-ui/core/';
import Box from '../components/Box';
import { makeStyles } from '@material-ui/core/styles';
import HomeOutlinedIcon from '@material-ui/icons/HomeOutlined';
import WorkOutlineOutlinedIcon from '@material-ui/icons/WorkOutlineOutlined';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  profileImage: {
    height: 150,
    width: 150,
    borderRadius: '50%',
    margin: '20px 0'
  },
  flexBox: {
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'column'
  }
}));

export default function Account() {
  const [password, setPassword] = useState('');
  const classes = useStyles();

  return (
    <div>
      <Typography variant="h4">Account</Typography>
      <Divider style={{ marginBottom: 20 }} />

      <Grid container spacing={2}>
        <Grid item xs={false} sm={12} md={4} lg={3}>
          <Box>
            <Typography variant="h5">Profile Details</Typography>
            <div className={classes.flexBox}>
              <img
                src={require('../assets/images/alec.jpg')}
                alt="profile"
                className={classes.profileImage}
              />
              <HomeOutlinedIcon />
              <Typography variant="subtitle1">Alec Down</Typography>
              <WorkOutlineOutlinedIcon />
              <Typography variant="subtitle2">Developer</Typography>
            </div>
          </Box>
        </Grid>
        <Grid item xs={false} sm={12} md={4} lg={3}>
          <Box>
            <Typography variant="h5">About</Typography>
            <Typography variant="subtitle1">
              Lives in <span></span>
            </Typography>
            <Typography variant="subtitle1">Works at</Typography>
          </Box>
        </Grid>
        <Grid item xs={false} sm={12} md={6} lg={4}>
          <Box>
            <Typography variant="h5">Reset Password</Typography>
            <Typography variant="p">Please enter your current password to get started.</Typography>
            <TextField
              required
              variant="outlined"
              margin="normal"
              id="password"
              label="Password"
              name="password"
              type="password"
              autoComplete="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </Box>
        </Grid>
      </Grid>
    </div>
  );
}
