import axios from 'axios';
import { GET_ERRORS, SET_USER_PROFILE } from './types';

export const updatePassword = passwordInformation => {
  axios
    .put('/api/profile/password')
    .then(res => {
      dispatch({
        type: CREATE_MESSAGE,
        payload: {
          registration: 'Password was successfully updated.'
        }
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      });
    });
};

export const updateUserProfile = profileInformation => dispatch => {
  axios
    .put('/api/profile', profileInformation)
    .then(res => {
      dispatch({
        type: SET_USER_PROFILE,
        payload: res.data
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      });
    });
};

export const getUserProfile = () => dispatch => {
  axios
    .get('/api/profile')
    .then(res => {
      dispatch({
        type: SET_USER_PROFILE,
        payload: res.data
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      });
    });
};
